import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable} from 'rxjs';
import { Employee } from './employee';



/* Merkataan luokka EmployeeService - Angular Service:ksi */

@Injectable({
  providedIn: 'root'
})
export class EmployeeService {

  // Java Spring backend-projektin URL tuodaan tähän.

  /* BASE URL kaikille REST-ohjelmointirajapinnoille */

  private baseURL = "http://localhost:8080/api/v1/employees";


  /* Tuodaan HttpClient-model, joka tekee REST-API pyyntöjä */

  constructor(private httpClient: HttpClient) { }


  /* Lähettää GET-pyynnön tietokantaan. */

  getEmployeesList(): Observable<Employee[]>{
    return this.httpClient.get<Employee[]>(`${this.baseURL}`);    // (`${this.baseURL}`)
  }

  /* Lähettää POST-pyynnön tietokantaan. */

  createEmployee(employee: Employee): Observable<Object>{     // POST-request

    return this.httpClient.post(`${this.baseURL}`, employee)

  }

  /* Lähettää GET-pyynnön tietokantaan. */

  getEmployeeById(id: number): Observable<Employee>{

    return this.httpClient.get<Employee>(`${this.baseURL}/${id}`);

  }

  /* Lähettää PUT-pyynnön tietokantaan. */

  updateEmployee(id: number, employee: Employee): Observable<Object>{
    return this.httpClient.put(`${this.baseURL}/${id}`, employee)
  }

  /* Lähettää DELETE-pyynnön tietokantaan. */

  deleteEmployee(id: number): Observable<Object>{
    return this.httpClient.delete(`${this.baseURL}/${id}`);
  }


}
